	@Test
	public void GetByKeyword() {
		InstagramSource source = new InstagramSource(new GenericClient());
		
		List<Image> images = source.getByKeyword("Painting", 10);
		assertEquals(10, images.size());
		
		assertEquals("Got the URL from the first object",
					 "http://distilleryimage6.s3.amazonaws.com/02e2d170456311e3860d22000aa81037_8.jpg", images.get(0).getUrl());

		assertEquals("Got the URL from the second object",
					 "http://distilleryimage5.s3.amazonaws.com/f95933b0260911e3aa5e22000a1f96ec_7.jpg", images.get(1).getUrl());

		assertEquals("Got the ID as an int",
					 581816274693053996L, images.get(0).getID());

		// assertEquals("Got correct parsed date",
		//  			 598260688L, images.get(0).getCreatedTime().getTime());

		assertEquals("Got correct description",
					 "That backhand thoooo ðŸ”¥", images.get(1).getDescription());
	}
